func (_q *UserHouseholdQuery) CollectFields(ctx context.Context, satisfies ...string) (*UserHouseholdQuery, error) {
	fc := graphql.GetFieldContext(ctx)
	if fc == nil {
		return _q, nil
	}
	if err := _q.collectField(ctx, false, graphql.GetOperationContext(ctx), fc.Field, nil, satisfies...); err != nil {
		return nil, err
	}
	return _q, nil
}

func (_q *UserHouseholdQuery) collectField(ctx context.Context, oneNode bool, opCtx *graphql.OperationContext, collected graphql.CollectedField, path []string, satisfies ...string) error {
	path = append([]string(nil), path...)
	var (
		unknownSeen    bool
		fieldSeen      = make(map[string]struct{}, len(userhousehold.Columns))
		selectedFields = []string{userhousehold.FieldID}
	)
	for _, field := range graphql.CollectFields(opCtx, collected.Selections, satisfies) {
		switch field.Name {

		case "user":
			var (
				alias = field.Alias
				path  = append(path, alias)
				query = (&UserClient{config: _q.config}).Query()
			)
			if err := query.collectField(ctx, oneNode, opCtx, field, path, mayAddCondition(satisfies, userImplementors)...); err != nil {
				return err
			}
			_q.withUser = query
			if _, ok := fieldSeen[userhousehold.FieldUserID]; !ok {
				selectedFields = append(selectedFields, userhousehold.FieldUserID)
				fieldSeen[userhousehold.FieldUserID] = struct{}{}
			}

		case "household":
			var (
				alias = field.Alias
				path  = append(path, alias)
				query = (&HouseholdClient{config: _q.config}).Query()
			)
			if err := query.collectField(ctx, oneNode, opCtx, field, path, mayAddCondition(satisfies, householdImplementors)...); err != nil {
				return err
			}
			_q.withHousehold = query
			if _, ok := fieldSeen[userhousehold.FieldHouseholdID]; !ok {
				selectedFields = append(selectedFields, userhousehold.FieldHouseholdID)
				fieldSeen[userhousehold.FieldHouseholdID] = struct{}{}
			}
		case "createTime":
			if _, ok := fieldSeen[userhousehold.FieldCreateTime]; !ok {
				selectedFields = append(selectedFields, userhousehold.FieldCreateTime)
				fieldSeen[userhousehold.FieldCreateTime] = struct{}{}
			}
		case "updateTime":
			if _, ok := fieldSeen[userhousehold.FieldUpdateTime]; !ok {
				selectedFields = append(selectedFields, userhousehold.FieldUpdateTime)
				fieldSeen[userhousehold.FieldUpdateTime] = struct{}{}
			}
		case "userID":
			if _, ok := fieldSeen[userhousehold.FieldUserID]; !ok {
				selectedFields = append(selectedFields, userhousehold.FieldUserID)
				fieldSeen[userhousehold.FieldUserID] = struct{}{}
			}
		case "householdID":
			if _, ok := fieldSeen[userhousehold.FieldHouseholdID]; !ok {
				selectedFields = append(selectedFields, userhousehold.FieldHouseholdID)
				fieldSeen[userhousehold.FieldHouseholdID] = struct{}{}
			}
		case "role":
			if _, ok := fieldSeen[userhousehold.FieldRole]; !ok {
				selectedFields = append(selectedFields, userhousehold.FieldRole)
				fieldSeen[userhousehold.FieldRole] = struct{}{}
			}
		case "id":
		case "__typename":
		default:
			unknownSeen = true
		}
	}
	if !unknownSeen {
		_q.Select(selectedFields...)
	}
	return nil
}

type userhouseholdPaginateArgs struct {
	first, last   *int
	after, before *Cursor
	opts          []UserHouseholdPaginateOption
}

func newUserHouseholdPaginateArgs(rv map[string]any) *userhouseholdPaginateArgs {
	args := &userhouseholdPaginateArgs{}
	if rv == nil {
		return args
	}
	if v := rv[firstField]; v != nil {
		args.first = v.(*int)
	}
	if v := rv[lastField]; v != nil {
		args.last = v.(*int)
	}
	if v := rv[afterField]; v != nil {
		args.after = v.(*Cursor)
	}
	if v := rv[beforeField]; v != nil {
		args.before = v.(*Cursor)
	}
	if v, ok := rv[whereField].(*UserHouseholdWhereInput); ok {
		args.opts = append(args.opts, WithUserHouseholdFilter(v.Filter))
	}
	return args
}

const (
	afterField     = "after"
	firstField     = "first"
	beforeField    = "before"
	lastField      = "last"
	orderByField   = "orderBy"
	directionField = "direction"
	fieldField     = "field"
	whereField     = "where"
)

func fieldArgs(ctx context.Context, whereInput any, path ...string) map[string]any {
	field := collectedField(ctx, path...)
	if field == nil || field.Arguments == nil {
		return nil
	}
	oc := graphql.GetOperationContext(ctx)
	args := field.ArgumentMap(oc.Variables)
	return unmarshalArgs(ctx, whereInput, args)
}

// unmarshalArgs allows extracting the field arguments from their raw representation.
func unmarshalArgs(ctx context.Context, whereInput any, args map[string]any) map[string]any {
	for _, k := range []string{firstField, lastField} {
		v, ok := args[k]
		if !ok || v == nil {
			continue
		}
		i, err := graphql.UnmarshalInt(v)
		if err == nil {
			args[k] = &i
		}
	}
	for _, k := range []string{beforeField, afterField} {
		v, ok := args[k]
		if !ok {
			continue
		}
		c := &Cursor{}
		if c.UnmarshalGQL(v) == nil {
			args[k] = c
		}
	}
	if v, ok := args[whereField]; ok && whereInput != nil {
		if err := graphql.UnmarshalInputFromContext(ctx, v, whereInput); err == nil {
			args[whereField] = whereInput
		}
	}

	return args
}

// mayAddCondition appends another type condition to the satisfies list
// if it does not exist in the list.
func mayAddCondition(satisfies []string, typeCond []string) []string {
Cond:
	for _, c := range typeCond {
		for _, s := range satisfies {
			if c == s {
				continue Cond
			}
		}
		satisfies = append(satisfies, c)
	}
	return satisfies
}
