-- create "user_keys" table
CREATE TABLE "user_keys" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY (START WITH 42949672960), "create_time" timestamptz NOT NULL, "update_time" timestamptz NOT NULL, "provider" character varying NOT NULL, "key" character varying NOT NULL, "user_keys" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "user_keys_users_keys" FOREIGN KEY ("user_keys") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- create index "userkey_provider_key" to table: "user_keys"
CREATE UNIQUE INDEX "userkey_provider_key" ON "user_keys" ("provider", "key");
