-- create "investment_lots" table
CREATE TABLE "investment_lots" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY (START WITH 47244640256), "create_time" timestamptz NOT NULL, "update_time" timestamptz NOT NULL, "amount" numeric(36,18) NOT NULL, "price" numeric(36,18) NOT NULL, "household_id" bigint NOT NULL, "investment_investment_lots" bigint NOT NULL, "transaction_investment_lots" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "investment_lots_households_investment_lots" FOREIGN KEY ("household_id") REFERENCES "households" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "investment_lots_investments_investment_lots" FOREIGN KEY ("investment_investment_lots") REFERENCES "investments" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "investment_lots_transactions_investment_lots" FOREIGN KEY ("transaction_investment_lots") REFERENCES "transactions" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
