-- create "recurring_subscriptions" table
CREATE TABLE "recurring_subscriptions" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY (START WITH 51539607552), "create_time" timestamptz NOT NULL, "update_time" timestamptz NOT NULL, "name" character varying NOT NULL, "interval" character varying NOT NULL, "interval_count" bigint NOT NULL DEFAULT 1, "start_date" date NOT NULL, "active" boolean NOT NULL DEFAULT true, "icon" character varying NULL, "cost" numeric(36,18) NOT NULL, "fx_rate" numeric(36,18) NOT NULL, "currency_id" bigint NOT NULL, "household_id" bigint NOT NULL, "user_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "recurring_subscriptions_currencies_recurring_subscriptions" FOREIGN KEY ("currency_id") REFERENCES "currencies" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "recurring_subscriptions_households_recurring_subscriptions" FOREIGN KEY ("household_id") REFERENCES "households" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "recurring_subscriptions_users_recurring_subscriptions" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
